import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { CatagorymgmtComponent } from 'src/app/admin/catagorymgmt/catagorymgmt.component';
import { AppComponent } from 'src/app/app.component';

@Component({
  selector: 'app-product',
  templateUrl: './product.component.html',
  styleUrls: ['./product.component.css']
})
export class ProductComponent {
  list: any = []; // All Products
  list2: any = []; // All Categories

  name: string = '';
  price: number = 0;
  quantity: number = 0;
  description: string = '';
  discount: number = 0;
  catid: number = 0;

  constructor(public http: HttpClient, public app: AppComponent, public cata: CatagorymgmtComponent) { }

  ngOnInit(): void {
    this.loadCategories(); // Load categories when the component initializes
    this.loadProduct(); // Load products when the component initializes
  }

  loadProduct(): void {
    const url = `${this.app.baseUrl}seller/getAllProducts/${this.app.id}`;
    this.http.get(url).subscribe(data => {
      if (data == null) {
        window.alert('Something is Wrong');
      } else {
        this.list = data;
      }
    });
  }

  loadCategories(): void {
    const url = `${this.app.baseUrl}admin/getAllCategories`;
    this.http.get<any[]>(url).subscribe(data => {
      if (data == null) {
        window.alert('Something is Wrong');
      } else {
        this.list2 = data;
      }
    });
  }

  addProduct(): void {
    if (!this.name.trim()) {
      window.alert('Please enter a product name');
      return;
    }

    const product = {
      name: this.name,
      userid: this.app.id,
      price: this.price,
      quantity: this.quantity,
      description: this.description,
      discount: this.discount,
      catid: this.catid
    };

    const url = `${this.app.baseUrl}seller/addNewProduct`;
    this.http.post(url, product).subscribe((data: any) => {
      if (data == null) {
        window.alert('Something is wrong');
      } else {
        this.list.push(data);
        this.resetForm();
      }
    });
  }

  resetForm(): void {
    this.name = '';

  }
}
