  categories: any;
  catid: number = 0;
  minprice: number = 0;
  maxprice: number = 60000;
  minrating: number = 0;
  star: number = 0
  cartItems: any;
  showCart: boolean = false;
  constructor(public http: HttpClient, public app: AppComponent) {
    let url2 = app.baseUrl + 'admin/getAllCategories'
    http.get(url2).subscribe((data: any) => {
      if (data == null) {
        window.alert('Something is wrong')
      }
      else {
        this.categories = data;
      }
    });
  }
  products: any
  obj: any
  loadProduct() {
    let obj = [this.catid, this.minprice, this.maxprice, this.minrating];
    let url = this.app.baseUrl + 'buyer/getProductsBuyer';
    this.http.post(url, obj).subscribe((data: any) => {
      if (data == null || data.length === 0) {
        window.alert('Something is wrong')
      }
      else {
        this.products = data;
      }
    });
  }
  rating: number = 0;
  printRating(p: any): void {
    if (p.star !== undefined) { // Ensure p.star is defined before proceeding
      let url = `${this.app.baseUrl}buyer/giveRatingToProduct/${p.id}/${this.app.id}/${p.star}`;
      this.http.get(url).subscribe((data: any) => {
        if (data == null || data == 0) {
          window.alert('Something went wrong while updating rating.');
        } else {
          this.loadProduct();
          window.alert('Rating updated successfully.');
        }
      });
    }
  }
  AddToCart(p: any) {
    const obj = {
      userid: this.app.id,
      productid: p.id
    };
    const url = ` ${this.app.baseUrl}buyer/addToCart`;
    this.http.post(url, obj).subscribe((data: any) => {
      if (data == null) {
        window.alert('Something is wrong');
      } else {
        window.alert('Added to cart');
      }
    });
  }
  loadCartProducts() {
    let url = this.app.baseUrl + 'buyer/getCartProducts' + this.app.id;
    this.http.get(url).subscribe((data: any) => {
      if (data == null) {
        window.alert('Something is wrong')
      }
      else {
        this.cartItems = data;
        this.showCart = true;
      }
    });
  }
  cartProduct() {
    this.loadCartProducts();
  }
}
