import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { AppComponent } from 'src/app/app.component';

@Component({
  selector: 'app-showproducts',
  templateUrl: './showproducts.component.html',
  styleUrls: ['./showproducts.component.css']
})
export class ShowproductsComponent implements OnInit {
  categories: any;
  products: any;
  catid: number = 0;
  minprice: number = 0;
  maxprice: number = 60000;
  minrating: number = 0;
  star: number = 0;
  cartItems: any;
  showCart: boolean = false;
    constructor(private http: HttpClient, private app: AppComponent) { }

    ngOnInit(): void {
      this.loadCategories();
    }

    loadCategories(): void {
      const url = `${this.app.baseUrl}admin/getAllCategories`;
      this.http.get(url).subscribe({
        next: (data: any) => this.categories = data,
        error: () => window.alert('Something is wrong')
      });
    }

    loadProduct(): void {
      const obj = { catid: this.catid, minprice: this.minprice, maxprice: this.maxprice, minrating: this.minrating };
      const url = `${this.app.baseUrl}buyer/getProductsBuyer`;
      this.http.post(url, obj).subscribe({
        next: (data: any) => {
          if (data && data.length > 0) {
            this.products = data;
          } else {
            window.alert('No products found with the given criteria.');
          }
        },
        error: () => window.alert('Something is wrong')
      });
    }

    printRating(p: any): void {
      if (p.stars !== undefined) {
        const url = `${this.app.baseUrl}buyer/giveRatingToProduct/${p.id}/${this.app.id}/${p.stars}`;
        this.http.get(url).subscribe({
          next: (data: any) => {
            if (data) {
              this.loadProduct();
              window.alert('Rating updated successfully.');
            } else {
              window.alert('Something went wrong while updating rating.');
            }
          },
          error: () => window.alert('Something went wrong while updating rating.')
        });
      }
    }

    addToCart(p: any): void {
      const url = `${this.app.baseUrl}buyer/addToCart/${p.id}/${this.app.id}`;
      this.http.post(url, {}).subscribe({
        next: (data: any) => {
          if (data) {
            window.alert('Added to cart');
          } else {
            window.alert('Something is wrong');
          }
        },
        error: () => window.alert('Something is wrong')
      });
    }

    loadCartProducts(): void {
      const url = `${this.app.baseUrl}buyer/getCartProducts/${this.app.id}`;
      this.http.get(url).subscribe({
        next: (data: any) => {
          if (data) {
            this.cartItems = data;
            this.showCart = true;
          } else {
            window.alert('Something is wrong');
          }
        },
        error: () => window.alert('Something is wrong')
      });
    }

    cartProduct(): void {
      this.loadCartProducts();
    }

    getTotalAmount(): number {
      return this.cartItems.reduce((total, item) => total + (item.price - item.discount / 100) * item.quantity, 0);
    }
  }
