import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { AppComponent } from '../app.component';

@Component({
  selector: 'app-seller',
  templateUrl: './seller.component.html',
  styleUrls: ['./seller.component.css']
})
export class SellerComponent {

  whatToShow: number = 0;
  products: any =null;
  categories: any[] = [];
  name: string = '';

 constructor(public http: HttpClient, public app: AppComponent) {
    let url = app.baseUrl + 'login/getName' + app.id;
    http.get(url).subscribe((data: any) => {
      if (data == null) {
        window.alert('something is wrong');
      }
      else {
        this.name = data[0];
      }
    })
  }

  changeWhatToShow(num: number) {
    this.whatToShow = num;
    this.loadProduct();
    return;
  }

  loadProduct() {
    const url = this.app.baseUrl + 'eller/getAllProducts'+this.app.id;
    this.http.get(url).subscribe(data => {
      if (data == null) {
        window.alert('Something is Wrong');
      } else {
        this.products = data;
      }
    });
  }

  loadCategories() {
    const url = this.app.baseUrl + 'admin/getAllCategories';
    this.http.get<any[]>(url).subscribe(data => {
      if (data == null) {
        window.alert('Something is Wrong');
      } else {
        this.categories = data;
      }
    });
  }

  ngOnInit(): void {
    this.loadCategories(); // Load categories when the component initializes
  }
}
