import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { AppComponent } from 'src/app/app.component';

@Component({
  selector: 'app-showproducts',
  templateUrl: './showproducts.component.html',
  styleUrls: ['./showproducts.component.css']
})
export class ShowproductsComponent {

  categories: any;
  products:any;
  catid: number = 0;
  constructor(public http: HttpClient, public app: AppComponent) {
    const url2 = `${this.app.baseUrl}admin/getAllCategories`;
    this.http.get<any[]>(url2).subscribe(data => {
      if (data == null) {
        window.alert('Something is Wrong');
      } else {
        // console.log(data)
        this.categories = data;
      }
    });
  }

  // ngOnInit(): void {
  //   this.loadCategories(); // Load categories when the component initializes
  //   // this.loadProduct(); // Load products when the component initializes
  // }

  // loadCategories() {
  //   const url = `${this.app.baseUrl}admin/getAllCategories`;
  //   this.http.get<any[]>(url).subscribe(data => {
  //     if (data == null) {
  //       window.alert('Something is Wrong');
  //     } else {
  //       // console.log(data)
  //       this.categories = data;
  //     }
  //   });
  // }
  loadProduct() {
    const url = this.app.baseUrl + 'seller/getAllProducts' + this.app.id;
    this.http.get(url).subscribe(data => {
      // console.log(data)
      if (data == null) {
        window.alert('Something is Wrong');
      } else {
        this.products = data;
      }
    });
  }
}
