import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { AppComponent } from 'src/app/app.component';

@Component({
  selector: 'app-showproducts',
  templateUrl: './showproducts.component.html',
  styleUrls: ['./showproducts.component.css']
})
export class ShowproductsComponent implements OnInit {
  categories: any;
  products: any;
  catid: number = 0;
  minprice: number = 0;
  maxprice: number = 60000;
  minrating: number = 0;
  cartItems: any;
  rating: number = 0;
  showCart: boolean = false;
  orders: any[] = [];
  constructor(private http: HttpClient, private app: AppComponent) { }

  ngOnInit(): void {
    this.loadCategories();
  }

  loadCategories(): void {
    const url = `${this.app.baseUrl}admin/getAllCategories`;
    this.http.get(url).subscribe({
      next: (data: any) => this.categories = data,
      error: () => window.alert('Something is wrong')
    });
  }

  loadProduct(): void {
    const obj = [this.catid, this.minprice, this.maxprice, this.minrating];
    const url = `${this.app.baseUrl}buyer/getProductsBuyer`;
    this.http.post(url, obj).subscribe({
      next: (data: any) => {
        if (data && data.length > 0) {
          this.products = data;
        } else {
          window.alert('No products found with the given criteria.');
        }
      },
      error: () => window.alert('Something is wrong')
    });
  }

  printRating(p: any): void {
    if (p.rating != 0) {
      const url = `${this.app.baseUrl}buyer/giveRatingToProduct/${p.id}/${this.app.id}/${p.rating}`;
      this.http.get(url).subscribe((data: any) => {
        // console.log(data)
        if (data) {
          // this.loadProduct();
          // console.log(this.products)
          window.alert('Rating updated successfully.');
        } else {
          window.alert('Something went wrong while updating rating.');
        }
      });
    }
  }

  addToCart(p: any): void {
    const url = `${this.app.baseUrl}buyer/addToCart/${p.id}/${this.app.id}`;
    this.http.post(url, {}).subscribe({
      next: (data: any) => {
        if (data) {
          window.alert('Added to cart');
        } else {
          window.alert('Something is wrong');
        }
      },
      error: () => window.alert('Something is wrong')
    });
  }

  loadCartProducts(): void {
    const url = `${this.app.baseUrl}buyer/getCartProducts/${this.app.id}`;
    this.http.get(url).subscribe({
      next: (data: any) => {
        if (data) {
          this.cartItems = data;
          this.showCart = true;
        } else {
          window.alert('Something is wrong');
        }
      },
      error: () => window.alert('Something is wrong')
    });
  }

  cartProduct(): void {
    this.loadCartProducts();
  }

  getTotalAmount(): number {
    return this.cartItems.reduce((total: number, item: {
      price: number; discount: number; quantity: number;
    }) => total + (item.price - item.discount / 100) * item.quantity, 0);
  }

  placeOrder() {
    for (let i = 0; i < this.cartItems.length; i++) {

      let myArray: any[] = new Array(2);
      myArray[0] = this.cartItems[i].id;
      myArray[1] = this.cartItems[i].quantity;
      console.log(myArray);
      this.orders.push(myArray);
    }
    let url3 = this.app.baseUrl + "buyer/onPlaceOrder/" + this.app.id;
    this.http.post(url3, this.orders).subscribe((data: any) => {
      console.log(data)
      if (data == null || data == -1) {
        window.alert("Something went Wrong..")
      }
      else {
        window.alert("Your Order Placed SuccessFulyy...")
        this.cartItems = [];
      }
    });
  }
}
